create view high_amount as 
select B.BranchNumber, C.AadharNumber, C.Name, L.LoanNumber
from BANKBRANCH as B, CUSTOMER as C, LOAN as L
where L.AadharNumber = C.AadharNumber and L.BranchNumber = B.BranchNumber
and L.Amount > 10000000;

select * from high_amount;


create view check_managers as 
select E.EmployeeName, E.MobileNumber, M.BranchNumber
from EMPLOYEE as E, MANAGES as M, BANKBRANCH as B
where E.EmployeeID = M.EmployeeID and M.BranchNumber = B.BranchNumber
and B.BranchNumber in (9,10,11,12,13,14);

select* from check_managers;

